// if the option is required add it to the required list
if (opt.isRequired() )
{
if( requiredOpts.contains(key) ) {
requiredOpts.remove( requiredOpts.indexOf(key) );
}
requiredOpts.add(key);
}

shortOpts.put(key, opt);

return this;
}

