return requiredOptions;
}

public CommandLine parse(Options options, String[] arguments)
throws ParseException
{
return parse(options, arguments, null, false);
}

public CommandLine parse(Options options, String[] arguments,
Properties properties, boolean stopAtNonOption)
throws ParseException
{
// clear out the data in options in case it's been used before (CLI-71)
for (Iterator it = options.helpOptions().iterator(); it.hasNext();) {
Option opt = (Option) it.next();
opt.clearValues();
}

// initialise members
setOptions(options);

cmd = new CommandLine();

boolean eatTheRest = false;

if (arguments == null)
{
arguments = new String[0];
}

List tokenList = Arrays.asList(flatten(getOptions(),
arguments,
stopAtNonOption));

ListIterator iterator = tokenList.listIterator();

// process each flattened token
while (iterator.hasNext())
{
String t = (String) iterator.next();

// the value is the double-dash
if ("--".equals(t))
{
eatTheRest = true;
}

// the value is a single dash
else if ("-".equals(t))
{
if (stopAtNonOption)
{
eatTheRest = true;
}
else
{
cmd.addArg(t);
}
}

// the value is an option
else if (t.startsWith("-"))
{
if (stopAtNonOption && !getOptions().hasOption(t))
{
eatTheRest = true;
cmd.addArg(t);
}
else
{
processOption(t, iterator);
}
}

// the value is an argument
else
{
cmd.addArg(t);

if (stopAtNonOption)
{
eatTheRest = true;
}
}

// eat the remaining tokens
if (eatTheRest)
{
while (iterator.hasNext())
{
String str = (String) iterator.next();

// ensure only one double-dash is added
if (!"--".equals(str))
{
cmd.addArg(str);
}
}
}
}

processProperties(properties);
checkRequiredOptions();

return cmd;
}

protected void processProperties(Properties properties)
{
if (properties == null)
{
return;
}

for (Enumeration e = properties.propertyNames(); e.hasMoreElements();)
{
String option = e.nextElement().toString();

if (!cmd.hasOption(option))
{
Option opt = getOptions().getOption(option);

// get the value from the properties instance
String value = properties.getProperty(option);

if (opt.hasArg())
{
if ((opt.getValues() == null)
|| (opt.getValues().length == 0))
{
try
{
opt.addValueForProcessing(value);
}
catch (RuntimeException exp)
{
// if we cannot add the value don't worry about it
}
}
}
else if (!("yes".equalsIgnoreCase(value)
|| "true".equalsIgnoreCase(value)
|| "1".equalsIgnoreCase(value)))
{
// if the value is not yes, true or 1 then don't add the
// option to the CommandLine
break;
}

cmd.addOption(opt);
}
}
}

protected void checkRequiredOptions()
throws MissingOptionException
{
// if there are required options that have not been
// processsed
if (getRequiredOptions().size() > 0)
{
Iterator iter = getRequiredOptions().iterator();
StringBuffer buff = new StringBuffer("Missing required option");
buff.append(getRequiredOptions().size() == 1 ? "" : "s");
buff.append(": ");


// loop through the required options
while (iter.hasNext())
{
buff.append(iter.next());
buff.append(", ");
}

throw new MissingOptionException(buff.substring(0, buff.length() - 2));
}
}

public void processArgs(Option opt, ListIterator iter)
throws ParseException
{
// loop until an option is found
while (iter.hasNext())
{
String str = (String) iter.next();

// found an Option, not an argument
if (getOptions().hasOption(str) && str.startsWith("-"))
{
iter.previous();
break;
}

// found a value
try
{
opt.addValueForProcessing( Util.stripLeadingAndTrailingQuotes(str) );
}
catch (RuntimeException exp)
{
iter.previous();
break;
}
}

if ((opt.getValues() == null) && !opt.hasOptionalArg())
{
throw new MissingArgumentException("Missing argument for option:"
+ opt.getKey());
}
}

protected void processOption(String arg, ListIterator iter)
throws ParseException
{
boolean hasOption = getOptions().hasOption(arg);

// if there is no option throw an UnrecognisedOptionException
if (!hasOption)
{
throw new UnrecognizedOptionException("Unrecognized option: "
+ arg);
}

// get the option represented by arg
final Option opt = getOptions().getOption(arg);

// if the option is a required option remove the option from
// the requiredOptions list
if (opt.isRequired())
{
getRequiredOptions().remove(opt.getKey());
}

// if the option is in an OptionGroup make that option the selected
// option of the group
if (getOptions().getOptionGroup(opt) != null)
{
OptionGroup group = getOptions().getOptionGroup(opt);

if (group.isRequired())
{
getRequiredOptions().remove(group);
}

group.setSelected(opt);
}

// if the option takes an argument value
if (opt.hasArg())
{
processArgs(opt, iter);
}


// set the option on the command line
cmd.addOption(opt);
}

CommandLine()
{
// nothing to do
}

public String getOptionValue(String opt)
{
String[] values = getOptionValues(opt);

return (values == null) ? null : values[0];
}

public String[] getOptionValues(String opt)
{
Option key = resolveOption( opt );

if (options.contains(key))
{
return key.getValues();
}

return null;
}

private Option resolveOption( String opt )
{
opt = Util.stripLeadingHyphens(opt);
for ( Iterator it = options.iterator(); it.hasNext(); )
{
Option option = (Option) it.next();
if (opt.equals(option.getOpt()))
{
return option;
}
if (opt.equals( option.getLongOpt()))
{
return option;
}

}
return null;
}

void addOption(Option opt)
{
options.add(opt);
}

protected String[] flatten(Options options, String[] arguments, boolean stopAtNonOption)
{
List tokens = new ArrayList();

boolean eatTheRest = false;

for (int i = 0; i < arguments.length; i++)
{
String arg = arguments[i];

if ("--".equals(arg))
{
eatTheRest = true;
tokens.add("--");
}
else if ("-".equals(arg))
{
tokens.add("-");
}
else if (arg.startsWith("-"))
{
String opt = Util.stripLeadingHyphens(arg);

if (options.hasOption(opt))
{
tokens.add(arg);
}
else
{
if (options.hasOption(arg.substring(0, 2)))
{
// the format is --foo=value or -foo=value
// the format is a special properties option (-Dproperty=value)
tokens.add(arg.substring(0, 2)); // -D
tokens.add(arg.substring(2)); // property=value
}
else
{
eatTheRest = stopAtNonOption;
tokens.add(arg);
}
}
}
else
{
tokens.add(arg);
}

if (eatTheRest)
{
for (i++; i < arguments.length; i++)
{
tokens.add(arguments[i]);
}
}
}

return (String[]) tokens.toArray(new String[tokens.size()]);
}

