if (preferredName == null) {
preferredName = name;
} else {
aliases.add(name);
}

if (burstEnabled && (name.length() == (shortPrefix.length() + 1))) {
burstAliases.add(name);
}

return this;
}

public DefaultOptionBuilder withLongName(final String longName) {
final String name = longPrefix + longName;

if (preferredName == null) {
preferredName = name;
} else {
