public boolean canProcess(final WriteableCommandLine commandLine,
final ListIterator arguments) {
if (arguments.hasNext()) {
final String argument = (String) arguments.next();
arguments.previous();

return canProcess(commandLine, argument);
} else {
return false;
}
}

public int getId() {
return id;
