if ((valueList == null) || valueList.isEmpty()) {
valueList = (List) this.defaultValues.get(option);
}
// if there are more default values as specified, add them to
// the list.
// copy the list first

return valueList == null ? Collections.EMPTY_LIST : valueList;
}

