// if there is a valid separator character
if (separator != NUL) {
final int initialIndex = arg.indexOf(separator);

// if there is a separator present
if (initialIndex > 0) {
return triggers.contains(arg.substring(0, initialIndex));
}
}
}

return triggers.contains(arg);
}

