if (displayAliases && !aliases.isEmpty()) {
buffer.append(" (");

final List list = new ArrayList(aliases);
Collections.sort(list);

for (final Iterator i = list.iterator(); i.hasNext();) {
final String alias = (String) i.next();
buffer.append(alias);

if (i.hasNext()) {
buffer.append(',');
}
}

buffer.append(')');
}

