{
// check the previous option before handling the next one
checkRequiredArgs();

option = (Option) option.clone();

updateRequiredOptions(option);

cmd.addOption(option);

if (option.hasArg())
{
currentOption = option;
}
else
{
currentOption = null;
}
}

