public void printUsage(PrintWriter pw, int width, String app, Options options)
{
// initialise the string buffer
StringBuffer buff = new StringBuffer(defaultSyntaxPrefix).append(app).append(" ");

// create a list for processed option groups
final Collection processedGroups = new ArrayList();

// temp variable
Option option;

List optList = new ArrayList(options.getOptions());
Collections.sort(optList, getOptionComparator());
// iterate over the options
for (Iterator i = optList.iterator(); i.hasNext();)
{
// get the next Option
option = (Option) i.next();

// check if the option is part of an OptionGroup
OptionGroup group = options.getOptionGroup(option);

// if the option is part of a group
if (group != null)
{
// and if the group has not already been processed
if (!processedGroups.contains(group))
{
// add the group to the processed list
processedGroups.add(group);


// add the usage clause
appendOptionGroup(buff, group);
}

// otherwise the option was displayed in the group
// previously so ignore it.
}

// if the Option is not part of an OptionGroup
else
{
appendOption(buff, option, option.isRequired());
}

if (i.hasNext())
{
buff.append(" ");
}
}


// call printWrapped
printWrapped(pw, width, buff.toString().indexOf(' ') + 1, buff.toString());
}

private void appendOption(final StringBuffer buff, final Option option, final boolean required)
{
if (!required)
{
buff.append("[");
}

if (option.getOpt() != null)
{
buff.append("-").append(option.getOpt());
}
else
{
buff.append("--").append(option.getLongOpt());
}

// if the Option has a value and a non blank argname
if (option.hasArg() && option.hasArgName())
{
buff.append(option.getOpt() == null ? longOptSeparator : " ");
buff.append("<").append(option.getArgName()).append(">");
}

// if the Option is not a required option
if (!required)
{
buff.append("]");
}
}

public void printWrapped(PrintWriter pw, int width, int nextLineTabStop, String text)
{
StringBuffer sb = new StringBuffer(text.length());

renderWrappedText(sb, width, nextLineTabStop, text);
pw.println(sb.toString());
}

protected StringBuffer renderWrappedText(StringBuffer sb, int width,
int nextLineTabStop, String text)
{
int pos = findWrapPos(text, width, 0);

if (pos == -1)
{
sb.append(rtrim(text));

return sb;
}
sb.append(rtrim(text.substring(0, pos))).append(defaultNewLine);

if (nextLineTabStop >= width)
{
// stops infinite loop happening
nextLineTabStop = 1;
}

// all following lines must be padded with nextLineTabStop space
// characters
final String padding = createPadding(nextLineTabStop);

while (true)
{
text = padding + text.substring(pos).trim();
pos = findWrapPos(text, width, 0);

if (pos == -1)
{
sb.append(text);

return sb;
}

if ((text.length() > width) && (pos == nextLineTabStop - 1))
{
pos = width;
}

sb.append(rtrim(text.substring(0, pos))).append(defaultNewLine);
}
}

protected int findWrapPos(String text, int width, int startPos)
{
int pos = -1;

// the line ends before the max wrap pos or a new line char found
if (((pos = text.indexOf('\n', startPos)) != -1 && pos <= width)
|| ((pos = text.indexOf('\t', startPos)) != -1 && pos <= width))
{
return pos + 1;
}
else if (startPos + width >= text.length())
{
return -1;
}


// look for the last whitespace character before startPos+width
pos = startPos + width;

char c;

while ((pos >= startPos) && ((c = text.charAt(pos)) != ' ')
&& (c != '\n') && (c != '\r'))
{
--pos;
}

// if we found it - just return
if (pos > startPos)
{
return pos;
}

// must look for the first whitespace chearacter after startPos
// + width
pos = startPos + width;

while ((pos <= text.length()) && ((c = text.charAt(pos)) != ' ')
&& (c != '\n') && (c != '\r'))
{
++pos;
}

return (pos == text.length()) ? (-1) : pos;
}

protected String rtrim(String s)
{
if ((s == null) || (s.length() == 0))
{
return s;
}

int pos = s.length();

while ((pos > 0) && Character.isWhitespace(s.charAt(pos - 1)))
{
--pos;
}

return s.substring(0, pos);
}

