private Token parseSimpleToken(final Token token, int ch) throws IOException {
if (readEndOfLine(ch)) {
token.type = EORECORD;
break;
} else if (isEndOfFile(ch)) {
} else if (isDelimiter(ch)) {
} else if (isEscape(ch)) {
token.content.append((char) ch);
ch = reader.read(); // continue
if (ignoreSurroundingSpaces) {
return token;
}

