private static int greatestCommonDivisor(int u, int v) {
if (Math.abs(u) <= 1 || Math.abs(v) <= 1) {
return 1;
if (u>0) { u=-u; } // make u negative
if (v>0) { v=-v; } // make v negative
int k=0;
while ((u&1)==0 && (v&1)==0 && k<31) { // while u and v are both even...
