915: assertEquals("Check 01:02:03.004", "2004-10-31 01:02:03.004 MDT", format.format(oct31_01_02_03_04MDT));
916: 917: // ------- Demonstrate Problem -------
918: Calendar gval = Calendar.getInstance();
919: gval.setTime(new Date(oct31_01MDT.getTime()));
920: gval.set(Calendar.MINUTE, gval.get(Calendar.MINUTE)); // set minutes to the same value
921: assertEquals("Demonstrate Problem", gval.getTime().getTime(), oct31_01MDT.getTime() + 3600000L);
922: 923: // ---------- Test Truncate ----------
924: assertEquals("Truncate Calendar.MILLISECOND",
925: oct31_01_02_03_04MDT, DateUtils.truncate(oct31_01_02_03_04MDT, Calendar.MILLISECOND));
926: 927: assertEquals("Truncate Calendar.SECOND",
928: oct31_01_02_03MDT, DateUtils.truncate(oct31_01_02_03_04MDT, Calendar.SECOND));
929: 930: assertEquals("Truncate Calendar.MINUTE",
