935: 936: assertEquals("Truncate Calendar.HOUR",
937: oct31_01MDT, DateUtils.truncate(oct31_01_02_03_04MDT, Calendar.HOUR));
938: 939: assertEquals("Truncate Calendar.DATE",
940: oct31MDT, DateUtils.truncate(oct31_01_02_03_04MDT, Calendar.DATE));
941: 942: 943: // ---------- Test Round (down) ----------
944: assertEquals("Round Calendar.MILLISECOND",
945: oct31_01_02_03_04MDT, DateUtils.round(oct31_01_02_03_04MDT, Calendar.MILLISECOND));
946: 947: assertEquals("Round Calendar.SECOND",
948: oct31_01_02_03MDT, DateUtils.round(oct31_01_02_03_04MDT, Calendar.SECOND));
949: 950: assertEquals("Round Calendar.MINUTE",
951: oct31_01_02MDT, DateUtils.round(oct31_01_02_03_04MDT, Calendar.MINUTE));
952: 953: assertEquals("Round Calendar.HOUR_OF_DAY",
954: oct31_01MDT, DateUtils.round(oct31_01_02_03_04MDT, Calendar.HOUR_OF_DAY));
955: 956: assertEquals("Round Calendar.HOUR",
957: oct31_01MDT, DateUtils.round(oct31_01_02_03_04MDT, Calendar.HOUR));
958: 959: assertEquals("Round Calendar.DATE",
960: oct31MDT, DateUtils.round(oct31_01_02_03_04MDT, Calendar.DATE));
