}

public static int gcd(final int p, final int q) {
int u = p;
int v = q;
if ((u == 0) || (v == 0)) {
return (Math.abs(u) + Math.abs(v));
if (u > 0) {
u = -u;
if (v > 0) {
v = -v;
int k = 0;
while ((u & 1) == 0 && (v & 1) == 0 && k < 31) { // while u and v are
u /= 2;
v /= 2;
k++; // cast out twos.
if (k == 31) {
int t = ((u & 1) == 1) ? v : -(u / 2)/* B3 */;
while ((t & 1) == 0) { // while t is even..
t /= 2; // cast out twos
if (t > 0) {
