public DefaultOptionBuilder withShortName(final String shortName) {
final String name = shortPrefix + shortName;
if (preferredName == null) {
aliases.add(name);
if (burstEnabled && (name.length() == (shortPrefix.length() + 1))) {
burstAliases.add(name);
return this;
}

