private DisplaySetting(final String name) {
this.name = name;
this.hashCode = name.hashCode();
all.add(this);
}

public String toString() {
final StringBuffer buffer = new StringBuffer();
appendUsage(buffer, DisplaySetting.ALL, null);
return buffer.toString();
}

