public void testPropertyOptionFlags() throws Exception
{
Properties properties = new Properties();
properties.setProperty( "a", "true" );
properties.setProperty( "c", "yes" );
properties.setProperty( "e", "1" );
199: Parser parser = new PosixParser();
201: CommandLine cmd = parser.parse(opts, null, properties);
assertTrue( cmd.hasOption("a") );
assertTrue( cmd.hasOption("c") );
assertTrue( cmd.hasOption("e") );
206: 207: properties = new Properties();
properties.setProperty( "a", "false" );
properties.setProperty( "c", "no" );
properties.setProperty( "e", "0" );
212: cmd = parser.parse(opts, null, properties);
assertTrue( !cmd.hasOption("a") );
assertTrue( !cmd.hasOption("c") );
assertTrue( cmd.hasOption("e") ); // this option accepts as argument
217: 218: properties = new Properties();
properties.setProperty( "a", "TRUE" );
properties.setProperty( "c", "nO" );
properties.setProperty( "e", "TrUe" );
223: cmd = parser.parse(opts, null, properties);
assertTrue( cmd.hasOption("a") );
assertTrue( !cmd.hasOption("c") );
assertTrue( cmd.hasOption("e") );
228: 229: properties = new Properties();
properties.setProperty( "a", "just a string" );
properties.setProperty( "e", "" );
233: cmd = parser.parse(opts, null, properties);
assertTrue( !cmd.hasOption("a") );
assertTrue( !cmd.hasOption("c") );
assertTrue( cmd.hasOption("e") );
}

