private void handleProperties(Properties properties) throws ParseException
if (properties == null)
for (Enumeration e = properties.propertyNames(); e.hasMoreElements();)
String option = e.nextElement().toString();
if (!cmd.hasOption(option))
Option opt = options.getOption(option);
String value = properties.getProperty(option);
if (opt.hasArg())
else if (!("yes".equalsIgnoreCase(value)
handleOption(opt);
}

public CommandLine parse(Options options, String[] arguments, Properties properties) throws ParseException
return parse(options, arguments, properties, false);
}

public AlreadySelectedException(OptionGroup group, Option option)
this("The option '" + option.getKey() + "' was specified but an option from this group "
this.group = group;
this.option = option;
}

private void updateRequiredOptions(Option option) throws AlreadySelectedException
if (option.isRequired())
if (options.getOptionGroup(option) != null)
OptionGroup group = options.getOptionGroup(option);
if (group.isRequired())
group.setSelected(option);
}

public CommandLine parse(Options options, String[] arguments, Properties properties, boolean stopAtNonOption) throws ParseException
this.options = options;
this.stopAtNonOption = stopAtNonOption;
skipParsing = false;
currentOption = null;
expectedOpts = new ArrayList(options.getRequiredOptions());
for (Iterator it = options.getOptionGroups().iterator(); it.hasNext();)
OptionGroup group = (OptionGroup) it.next();
group.setSelected(null);
}
cmd = new CommandLine();
if (arguments != null)
for (int i = 0; i < arguments.length; i++)
handleToken(arguments[i]);
checkRequiredArgs();
handleProperties(properties);
}

public Options addOptionGroup(OptionGroup group)
Iterator options = group.getOptions().iterator();
if (group.isRequired())
while (options.hasNext())
Option option = (Option) options.next();
option.setRequired(false);
addOption(option);
optionGroups.put(option.getKey(), group);
}
return this;
}

public Option(String opt, String description) throws IllegalArgumentException
this(opt, null, false, description);
}
