private void resolveLocalFileHeaderData(Map<ZipArchiveEntry, NameAndComment>
for (ZipArchiveEntry ze : entries.keySet()) {
OffsetEntry offsetEntry = entries.get(ze);
long offset = offsetEntry.headerOffset;
archive.seek(offset + LFH_OFFSET_FOR_FILENAME_LENGTH);
byte[] b = new byte[SHORT];
archive.readFully(b);
int fileNameLen = ZipShort.getValue(b);
archive.readFully(b);
int extraFieldLen = ZipShort.getValue(b);
int lenToSkip = fileNameLen;
while (lenToSkip > 0) {
int skipped = archive.skipBytes(lenToSkip);
if (skipped <= 0) {
lenToSkip -= skipped;
}
byte[] localExtraData = new byte[extraFieldLen];
archive.readFully(localExtraData);
ze.setExtra(localExtraData);
offsetEntry.dataOffset = offset + LFH_OFFSET_FOR_FILENAME_LENGTH
if (entriesWithoutUTF8Flag.containsKey(ze)) {
String orig = ze.getName();
NameAndComment nc = entriesWithoutUTF8Flag.get(ze);
ZipUtil.setNameAndCommentFromExtraFields(ze, nc.name,
if (!orig.equals(ze.getName())) {
nameMap.remove(orig);
nameMap.put(ze.getName(), ze);
}
}

static ZipEncoding getZipEncoding(String name) {
if (isUTF8(name)) {
return UTF8_ZIP_ENCODING;
}

static boolean isUTF8(String encoding) {
if (encoding == null) {
encoding = System.getProperty("file.encoding");
return UTF8.equalsIgnoreCase(encoding)
}

private Map<ZipArchiveEntry, NameAndComment> populateFromCentralDirectory()
HashMap<ZipArchiveEntry, NameAndComment> noUTF8Flag =
positionAtCentralDirectory();
byte[] signatureBytes = new byte[WORD];
archive.readFully(signatureBytes);
long sig = ZipLong.getValue(signatureBytes);
if (sig != CFH_SIG && startsWithLocalFileHeader()) {
while (sig == CFH_SIG) {
readCentralDirectoryEntry(noUTF8Flag);
archive.readFully(signatureBytes);
sig = ZipLong.getValue(signatureBytes);
return noUTF8Flag;
}

private void positionAtCentralDirectory()
boolean found = tryToLocateSignature(MIN_EOCD_SIZE + ZIP64_EOCDL_LENGTH,
if (!found) {
positionAtCentralDirectory32();
}

private boolean tryToLocateSignature(long minDistanceFromEnd,
boolean found = false;
long off = archive.length() - minDistanceFromEnd;
final long stopSearching =
if (off >= 0) {
for (; off >= stopSearching; off--) {
archive.seek(off);
int curr = archive.read();
if (curr == -1) {
if (curr == sig[POS_0]) {
curr = archive.read();
if (curr == sig[POS_1]) {
curr = archive.read();
if (curr == sig[POS_2]) {
curr = archive.read();
if (curr == sig[POS_3]) {
found = true;
break;
if (found) {
archive.seek(off);
return found;
}

private void positionAtCentralDirectory32()
boolean found = tryToLocateSignature(MIN_EOCD_SIZE, MAX_EOCD_SIZE,
if (!found) {
skipBytes(CFD_LOCATOR_OFFSET);
byte[] cfdOffset = new byte[WORD];
archive.readFully(cfdOffset);
archive.seek(ZipLong.getValue(cfdOffset));
}

private void skipBytes(final int count) throws IOException {
int totalSkipped = 0;
while (totalSkipped < count) {
int skippedNow = archive.skipBytes(count - totalSkipped);
if (skippedNow <= 0) {
totalSkipped += skippedNow;
}
}

public static long getValue(byte[] bytes) {
return getValue(bytes, 0);
