private BitSet readAllOrBits(final DataInput header, final int size) throws IOException {
final int areAllDefined = header.readUnsignedByte();
if (areAllDefined != 0) {
bits = new BitSet(size);
for (int i = 0; i < size; i++) {
bits.set(i, true);
return bits;
}

private static long readUint64(final DataInput in) throws IOException {
long firstByte = in.readUnsignedByte();
int mask = 0x80;
long value = 0;
for (int i = 0; i < 8; i++) {
if ((firstByte & mask) == 0) {
return value | ((firstByte & (mask - 1)) << (8 * i));
long nextByte = in.readUnsignedByte();
value |= nextByte << (8 * i);
mask >>>= 1;
}

public ChecksumVerifyingInputStream(final Checksum checksum, final InputStream in,
final long size, final long expectedChecksum) {
this.checksum = checksum;
this.in = in;
this.expectedChecksum = expectedChecksum;
this.bytesRemaining = size;
}

public int read() throws IOException {
if (bytesRemaining <= 0) {
int ret = in.read();
if (ret >= 0) {
checksum.update(ret);
--bytesRemaining;
if (bytesRemaining == 0 && expectedChecksum != checksum.getValue()) {
return ret;
}

public int read(byte[] b, int off, int len) throws IOException {
int ret = in.read(b, off, len);
if (ret >= 0) {
checksum.update(b, off, ret);
bytesRemaining -= ret;
if (bytesRemaining <= 0 && expectedChecksum != checksum.getValue()) {
return ret;
}

public void close() throws IOException {
in.close();
}

