219: int count = 0;
220: while (entry != null) {
221: out = new FileOutputStream(new File(dir, String.valueOf(count)));
222: IOUtils.copy(is, out);
223: out.close();
224: out = null;
225: count++;
226: entry = is.getNextTarEntry();
227: }
228: } finally {
229: is.close();
230: if (out != null) {
231: out.close();
232: }
233: }
234: }

