entry.setSize(ZipUtil.adjustToLong(def.getTotalIn()));
entry.setCompressedSize(ZipUtil.adjustToLong(def.getTotalOut()));
entry.setCrc(realCrc);
def.reset();
written += entry.getCompressedSize();
if (raf != null) {
