private ZipExtraField[] getAllExtraFieldsNoCopy() {
if (extraFields == null) {
return getUnparseableOnly();
return unparseableExtra != null ? getMergedFields() : extraFields;
}

public byte[] getLocalFileDataExtra() {
final byte[] extra = getExtra();
return extra != null ? extra : EMPTY;
}

private void writeCounted(final byte[] data) throws IOException {
streamCompressor.writeCounted(data);
}

public void writeCounted(final byte[] data, final int offset, final int length) throws IOException {
writeOut(data, offset, length);
writtenToOutputStreamForLastEntry += length;
totalWrittenToOutputStream += length;
}

void deflate() throws IOException {
final int len = def.deflate(outputBuffer, 0, outputBuffer.length);
if (len > 0) {
writeCounted(outputBuffer, 0, len);
}

public static byte[] getBytes(final long value) {
final byte[] result = new byte[WORD];
putLong(value, result, 0);
return result;
}

public static byte[] getBytes(final int value) {
final byte[] result = new byte[2];
putShort(value, result, 0);
return result;
}
