69: public void testRead7ZipMultiVolumeArchiveForStream() throws IOException,
70: URISyntaxException {
71: 72: URL zip = getClass().getResource("/apache-maven-2.2.1.zip.001");
73: FileInputStream archive = new FileInputStream(
74: new File(new URI(zip.toString())));
75: ZipArchiveInputStream zi = null;
76: try {
77: zi = new ZipArchiveInputStream(archive,null,false);
78: 79: // these are the entries that are supposed to be processed
80: // correctly without any problems
81: for (int i = 0; i < ENTRIES.length; i++) {
82: assertEquals(ENTRIES[i], zi.getNextEntry().getName());
83: }
84: 85: // this is the last entry that is truncated
86: ArchiveEntry lastEntry = zi.getNextEntry();
87: assertEquals(LAST_ENTRY_NAME, lastEntry.getName());
88: byte [] buffer = new byte [4096];
89: 90: // before the fix, we'd get 0 bytes on this read and all
91: // subsequent reads thus a client application might enter
92: // an infinite loop after the fix, we should get an
93: // exception
94: try {
95: int read = 0;
96: while ((read = zi.read(buffer)) > 0) { }
97: fail("shouldn't be able to read from truncated entry");
98: } catch (IOException e) {
99: assertEquals("Truncated ZIP file", e.getMessage());
100: }
101: 102: // and now we get another entry, which should also yield
103: // an exception
104: try {
105: zi.getNextEntry();
106: fail("shouldn't be able to read another entry from truncated"
107: + " file");
108: } catch (IOException e) {
109: // this is to be expected
110: }
111: } finally {
112: if (zi != null) {
113: zi.close();
114: }
115: }
116: }

