private Token parseSimpleToken(final Token tkn, int c) throws IOException {
if (readEndOfLine(c)) {
tkn.type = EORECORD;
break;
} else if (isEndOfFile(c)) {
tkn.type = EOF;
tkn.isReady = true; // There is data at EOF
break;
} else if (isDelimiter(c)) {
tkn.type = TOKEN;
break;
} else if (isEscape(c)) {
final int unescaped = readEscape();
if (unescaped == Constants.END_OF_STREAM) { // unexpected char after escape
tkn.content.append((char) unescaped);
c = in.read(); // continue
} else {
tkn.content.append((char) c);
c = in.read(); // continue
if (ignoreSurroundingSpaces) {
return tkn;
}

public CSVFormatBuilder withIgnoreEmptyLines(final boolean ignoreEmptyLines) {
this.ignoreEmptyLines = ignoreEmptyLines;
return this;
}

public CSVFormat build() {
validate();
return new CSVFormat(delimiter, quoteChar, quotePolicy, commentStart, escape,
}

CSVFormat(final char delimiter, final Character quoteChar,
String nullToString, final String[] header) {
if (isLineBreak(delimiter)) {
this.delimiter = delimiter;
this.quoteChar = quoteChar;
this.quotePolicy = quotePolicy;
this.commentStart = commentStart;
this.escape = escape;
this.ignoreSurroundingSpaces = ignoreSurroundingSpaces;
this.ignoreEmptyLines = ignoreEmptyLines;
this.recordSeparator = recordSeparator;
this.nullToString = nullToString;
this.header = header == null ? null : header.clone();
}

Lexer(final CSVFormat format, final ExtendedBufferedReader in) {
this.format = format;
this.in = in;
this.delimiter = format.getDelimiter();
this.escape = mapNullToDisabled(format.getEscape());
this.quoteChar = mapNullToDisabled(format.getQuoteChar());
this.commmentStart = mapNullToDisabled(format.getCommentStart());
this.ignoreSurroundingSpaces = format.getIgnoreSurroundingSpaces();
this.ignoreEmptyLines = format.getIgnoreEmptyLines();
}

CSVLexer(final CSVFormat format, final ExtendedBufferedReader in) {
super(format, in);
}

boolean readEndOfLine(int c) throws IOException {
if (c == CR && in.lookAhead() == LF) {
return c == LF || c == CR;
}

public CSVFormatBuilder withEscape(final Character escape) {
if (isLineBreak(escape)) {
this.escape = escape;
return this;
}
