public void testIssue1107() {
String json = "{\n" +
"  \"inBig\": {\n" +
"    \"key\": [\n" +
"      { \"inSmall\": \"hello\" }\n" +
"    ]\n" +
"  }\n" +
"}";
BigClass bigClass = new Gson().fromJson(json, BigClass.class);
SmallClass small = bigClass.inBig.get("key").get(0);
assertNotNull(small);

