public void copyCurrentEvent(JsonParser p) throws IOException
JsonToken t = p.getCurrentToken();
if (t == null) {
switch (t.id()) {
NumberType n = p.getNumberType();
if (n == NumberType.INT) {
writeNumber(p.getIntValue());
}

