_symbols = symbols;
_buckets = buckets;
_size = size;
_hashSeed = hashSeed;
int arrayLen = (symbols.length);
_sizeThreshold = _thresholdSize(arrayLen);
_indexMask =  (arrayLen - 1);
_longestCollisionList = longestColl;
