public int hashCode() {
return attributes != null ? attributes.hashCode() : 0;
}

public Attribute(String key, String value) {
Validate.notEmpty(key);
Validate.notNull(value);
this.key = key.trim().toLowerCase();
this.value = value;
}

static String normaliseWhitespace(String text) {
text = text.replaceAll("\\s{2,}|(\\r\\n|\\r|\\n)", " "); // more than one space, and newlines to " "
return text;
}

static String stripLeadingWhitespace(String text) {
return text.replaceFirst("^\\s+", "");
}

static boolean lastCharIsWhitespace(StringBuilder sb) {
if (sb.length() == 0)
return false;
String lastChar = sb.substring(sb.length()-1, sb.length());
Validate.isTrue(lastChar.length() == 1); // todo: remove check
return lastChar.equals(" ");
}

private Selector(String query, Element root) {
Validate.notNull(query);
query = query.trim();
Validate.notEmpty(query);
Validate.notNull(root);
this.elements = new LinkedHashSet<Element>();
this.query = query;
this.root = root;
this.tq = new TokenQueue(query);
}
