public String chompTo(String seq) {
String data = consumeTo(seq);
matchChomp(seq);
return data;
}

public boolean equals(Object o) {
if (this == o) return true;
if (o == null || getClass() != o.getClass()) return false;
Tag tag = (Tag) o;
if (tagName != null ? !tagName.equals(tag.tagName) : tag.tagName != null) return false;
}

public static TextNode createFromEncoded(String encodedText, String baseUri) {
String text = Entities.unescape(encodedText);
return new TextNode(text, baseUri);
}

public boolean matchesCS(String seq) {
return queue.startsWith(seq, pos);
}

private void parseEndTag() {
tq.consume("</");
String tagName = tq.consumeTagName();
tq.chompTo(">");
if (tagName.length() != 0) {
Tag tag = Tag.valueOf(tagName);
if (!last().tag().isIgnorableEndTag(tag)) // skips </tr> if in <table>
popStackToClose(tag);
}

public boolean matches(String seq) {
int count = seq.length();
if (count > remainingLength())
return false;
while (--count >= 0) {
if (Character.toLowerCase(seq.charAt(count)) != Character.toLowerCase(queue.charAt(pos+count)))
return false;
return true;
}

public TextNode(String text, String baseUri) {
this.baseUri = baseUri;
this.text = text;
}
