public boolean matches(String seq) {
int len = seq.length();
if (len > queue.size())
return false;
List<Character> chars = queue.subList(0, len);
char[] seqChars = seq.toCharArray();
for (int i = 0; i < len; i++) {
Character found = Character.toLowerCase(chars.get(i));
Character check = Character.toLowerCase(seqChars[i]);
if (!found.equals(check))
return false;
return true;
}

public void consume(String seq) {
if (!matches(seq))
int len = seq.length();
if (len > queue.size())
for (int i = 0; i < len; i++) {
consume();
}

public String chompTo(String seq) {
String data = consumeTo(seq);
matchChomp(seq);
return data;
}

public TextNode(String text, String baseUri) {
super(baseUri);
attributes.put(TEXT_KEY, text);
}

private void parseEndTag() {
tq.consume("</");
String tagName = tq.consumeWord();
tq.chompTo(">");
if (tagName.length() != 0) {
Tag tag = Tag.valueOf(tagName);
popStackToClose(tag);
}

public String consumeWord() {
StringBuilder wordAccum = new StringBuilder();
while (!queue.isEmpty() && Character.isLetterOrDigit(queue.peek())) {
wordAccum.append(queue.removeFirst());
return wordAccum.toString();
}

private void parseTextNode() {
String text = tq.consumeTo("<");
TextNode textNode = TextNode.createFromEncoded(text, baseUri);
last().appendChild(textNode);
}

