593: 594: Elements containers = doc.select("div, li:matches([0-9,]+)");
595: assertEquals(2, containers.size());
596: assertEquals("div", containers.get(0).tagName());
597: assertEquals("li", containers.get(1).tagName());
598: assertEquals("123", containers.get(1).text());
599: }

