179: @Test public void preservesSpaceInTextArea() {
180: // preserve because the tag is marked as preserve white space
181: Document doc = Jsoup.parse("<textarea>\n\tOne\n\tTwo\n\tThree\n</textarea>");
182: String expect = "One\n\tTwo\n\tThree"; // the leading and trailing spaces are dropped as a convenience to authors
183: Element el = doc.select("textarea").first();
184: assertEquals(expect, el.text());
185: assertEquals(expect, el.val());
186: assertEquals(expect, el.html());
187: assertEquals("<textarea>\n\t" + expect + "\n</textarea>", el.outerHtml()); // but preserved in round-trip html
188: }

