} else if (name.equals("noscript")) {
} else if (name.equals("script")) {
} else if (name.equals("head")) {
return anythingElse(t, tb);
Token.EndTag end = t.asEndTag();
name = end.name();
if (name.equals("head")) {
tb.pop();
tb.transition(AfterHead);
