void outerHtml(StringBuilder accum) {
String html = StringEscapeUtils.escapeHtml(getWholeText());
if (parent() instanceof Element && !((Element) parent()).preserveWhitespace()) {
html = normaliseWhitespace(html);
if (!isBlank() && parentNode instanceof Element && ((Element) parentNode).tag().canContainBlock()  && siblingIndex() == 0)
indent(accum);
accum.append(html);
}

static String normaliseWhitespace(String text) {
text = text.replaceAll("\\s{2,}|(\\r\\n|\\r|\\n)", " "); // more than one space, and newlines to " "
return text;
}

private Selector(String query, Element root) {
Validate.notNull(query);
query = query.trim();
Validate.notEmpty(query);
Validate.notNull(root);
this.elements = new LinkedHashSet<Element>();
this.query = query;
this.root = root;
this.tq = new TokenQueue(query);
}

public static Elements select(String query, Element root) {
return new Selector(query, root).select();
}

private Elements select() {
tq.consumeWhitespace();
if (tq.matchesAny(combinators)) { // if starts with a combinator, use root as elements
addElements(findElements()); // chomp first element matcher off queue
while (!tq.isEmpty()) {
return new Elements(elements);
}

private Elements findElements() {
if (tq.matchChomp("#")) {
} else if (tq.matchChomp(".")) {
} else if (tq.matchesWord()) {
return byTag();
}

private Elements byTag() {
String tagName = tq.consumeWord();
Validate.notEmpty(tagName);
return root.getElementsByTag(tagName);
}
