public void add(Evaluator e) {
evaluators.add(e);
updateNumEvaluators();
}

public boolean matches(Element root, Element node) {
for (int i = 0; i < num; i++) {
Evaluator s = evaluators.get(i);
if (s.matches(root, node))
return true;
