397: +   "</body>"
398: + "</html>";
399: InputStream is = new ByteArrayInputStream(input.getBytes(Charset.forName("ASCII")));
400: 401: Document doc = Jsoup.parse(is, null, "http://example.com");
402: doc.outputSettings().escapeMode(Entities.EscapeMode.xhtml);
403: 404: String output = new String(doc.html().getBytes(doc.outputSettings().charset()), doc.outputSettings().charset());
405: 406: assertFalse("Should not have contained a '?'.", output.contains("?"));
407: assertTrue("Should have contained a '&#xa0;' or a '&nbsp;'.",
408: output.contains("&#xa0;") || output.contains("&nbsp;"));
