boolean process(Token t, HtmlTreeBuilder tb) {
if (isWhitespace(t)) {
switch (t.type) {
Token.EndTag end = t.asEndTag();
name = end.name();
if (name.equals("head")) {
tb.pop();
tb.transition(AfterHead);
return anythingElse(t, tb);
return true;
}

public static boolean isWhitespace(int c){
return c == ' ' || c == '\t' || c == '\n' || c == '\f' || c == '\r';
}

