private Selector(String query, Element root) {
Validate.notNull(query);
query = query.trim();
Validate.notEmpty(query);
Validate.notNull(root);
this.evaluator = QueryParser.parse(query);
this.root = root;
}

public static Elements select(String query, Element root) {
return new Selector(query, root).select();
}

private Elements select() {
return Collector.collect(evaluator, root);
}

private TokenQueue tq;
private List<Evaluator> evals = new ArrayList<Evaluator>();
private QueryParser(String query) {
this.query = query;
this.tq = new TokenQueue(query);
}

private static final Pattern NTH_AB = Pattern.compile("((\\+|-)?(\\d+)?)n(\\s*(\\+|-)?\\s*\\d+)?", Pattern.CASE_INSENSITIVE);
private static final Pattern NTH_B  = Pattern.compile("(\\+|-)?(\\d+)");
}

public static Evaluator parse(String query) {
QueryParser p = new QueryParser(query);
return p.parse();
}

Evaluator parse() {
tq.consumeWhitespace();
if (tq.matchesAny(combinators)) { // if starts with a combinator, use root as elements
findElements();
while (!tq.isEmpty()) {
boolean seenWhite = tq.consumeWhitespace();
if (tq.matchesAny(combinators)) {
combinator(tq.consume());
} else if (seenWhite) {
findElements(); // take next el, #. etc off queue
}
if (evals.size() == 1)
return evals.get(0);
return new CombiningEvaluator.And(evals);
}

private void combinator(char combinator) {
tq.consumeWhitespace();
String subQuery = consumeSubQuery(); // support multi > childs
Evaluator newEval = parse(subQuery); // the evaluator to add into target evaluator
boolean replaceRightMost = false;
if (evals.size() == 1) {
rootEval = currentEval = new CombiningEvaluator.And(evals);
evals.clear();
if (combinator == '>')
else if (combinator == ' ')
else if (combinator == '+')
else if (combinator == '~')
else if (combinator == ',') { // group or.
if (currentEval instanceof CombiningEvaluator.Or) {
or = new CombiningEvaluator.Or();
or.add(currentEval);
or.add(newEval);
currentEval = or;
}
if (replaceRightMost)
else rootEval = currentEval;
evals.add(rootEval);
}

private String consumeSubQuery() {
StringBuilder sq = new StringBuilder();
while (!tq.isEmpty()) {
if (tq.matches("("))
else if (tq.matches("["))
sq.append("[").append(tq.chompBalanced('[', ']')).append("]");
else if (tq.matchesAny(combinators))
sq.append(tq.consume());
return sq.toString();
}

private void findElements() {
if (tq.matchChomp("#"))
else if (tq.matchChomp("."))
else if (tq.matchesWord())
byTag();
else if (tq.matches("["))
byAttribute();
}
