public boolean isEmpty() {
return bufPos >= bufLength;
}

public char current() {
bufferUp();
return isEmpty() ? EOF : charBuf[bufPos];
}

char consume() {
bufferUp();
char val = isEmpty() ? EOF : charBuf[bufPos];
bufPos++;
return val;
