66: public void namespacePreservation() throws IOException {
67: File in = ParseTest.getFile("/htmltests/namespaces.xhtml");
68: org.jsoup.nodes.Document jsoupDoc;
69: jsoupDoc = Jsoup.parse(in, "UTF-8");
70: 71: Document doc;
72: org.jsoup.helper.W3CDom jDom = new org.jsoup.helper.W3CDom();
73: doc = jDom.fromJsoup(jsoupDoc);
74: 75: Node htmlEl = doc.getChildNodes().item(0);
76: assertEquals("http://www.w3.org/1999/xhtml", htmlEl.getNamespaceURI());
77: assertEquals("html", htmlEl.getLocalName());
78: assertEquals("html", htmlEl.getNodeName());
79: 80: // inherits default namespace
81: Node head = htmlEl.getFirstChild();
82: assertEquals("http://www.w3.org/1999/xhtml", head.getNamespaceURI());
83: assertEquals("head", head.getLocalName());
84: assertEquals("head", head.getNodeName());
85: 86: Node epubTitle = htmlEl.getChildNodes().item(2).getChildNodes().item(3);
87: assertEquals("Check", epubTitle.getTextContent());
88: assertEquals("http://www.idpf.org/2007/ops", epubTitle.getNamespaceURI());
89: assertEquals("title", epubTitle.getLocalName());
90: assertEquals("epub:title", epubTitle.getNodeName());
91: 92: Node xSection = epubTitle.getNextSibling().getNextSibling();
93: assertEquals("urn:test", xSection.getNamespaceURI());
94: assertEquals("section", xSection.getLocalName());
95: assertEquals("x:section", xSection.getNodeName());
96: 97: // https://github.com/jhy/jsoup/issues/977
98: // does not keep last set namespace
99: Node svg = xSection.getNextSibling().getNextSibling();
100: assertEquals("http://www.w3.org/2000/svg", svg.getNamespaceURI());
101: assertEquals("svg", svg.getLocalName());
102: assertEquals("svg", svg.getNodeName());
103: 104: Node path = svg.getChildNodes().item(1);
105: assertEquals("http://www.w3.org/2000/svg", path.getNamespaceURI());
106: assertEquals("path", path.getLocalName());
107: assertEquals("path", path.getNodeName());
108: 109: Node clip = path.getChildNodes().item(1);
110: assertEquals("http://example.com/clip", clip.getNamespaceURI());
111: assertEquals("clip", clip.getLocalName());
112: assertEquals("clip", clip.getNodeName());
113: assertEquals("456", clip.getTextContent());
114: 115: Node picture = svg.getNextSibling().getNextSibling();
116: assertEquals("http://www.w3.org/1999/xhtml", picture.getNamespaceURI());
117: assertEquals("picture", picture.getLocalName());
118: assertEquals("picture", picture.getNodeName());
119: 120: Node img = picture.getFirstChild();
121: assertEquals("http://www.w3.org/1999/xhtml", img.getNamespaceURI());
122: assertEquals("img", img.getLocalName());
123: assertEquals("img", img.getNodeName());
124: 125: }

