final int start = bufPos;
final int remaining = bufLength;
final char[] val = charBuf;
OUTER: while (bufPos < remaining) {
for (char c : chars) {
if (val[bufPos] == c)
break OUTER;
bufPos++;
return bufPos > start ? cacheString(charBuf, stringCache, start, bufPos -start) : "";
}
