final Buffer buffer = byteData; // .mark and rewind used to return Buffer, now ByteBuffer, so cast for backward compat
buffer.mark();
byte[] bom = new byte[4];
if (byteData.remaining() >= bom.length) {
byteData.get(bom);
buffer.rewind();
if (bom[0] == 0x00 && bom[1] == 0x00 && bom[2] == (byte) 0xFE && bom[3] == (byte) 0xFF || // BE
} else if (bom[0] == (byte) 0xFE && bom[1] == (byte) 0xFF || // BE
} else if (bom[0] == (byte) 0xEF && bom[1] == (byte) 0xBB && bom[2] == (byte) 0xBF) {
return null;
}

private ConstrainableInputStream(InputStream in, int bufferSize, int maxSize) {
super(in, bufferSize);
Validate.isTrue(maxSize >= 0);
this.maxSize = maxSize;
remaining = maxSize;
capped = maxSize != 0;
startTime = System.nanoTime();
}

public static ConstrainableInputStream wrap(InputStream in, int bufferSize, int maxSize) {
return in instanceof ConstrainableInputStream
}

