140: @Test public void treatsUndeclaredNamespaceAsLocalName() {
141: String html = "<fb:like>One</fb:like>";
142: org.jsoup.nodes.Document doc = Jsoup.parse(html);
143: 144: Document w3Doc = new W3CDom().fromJsoup(doc);
145: Node htmlEl = w3Doc.getFirstChild();
146: 147: assertNull(htmlEl.getNamespaceURI());
148: assertEquals("html", htmlEl.getLocalName());
149: assertEquals("html", htmlEl.getNodeName());
150: 151: Node fb = htmlEl.getFirstChild().getNextSibling().getFirstChild();
152: assertNull(fb.getNamespaceURI());
153: assertEquals("like", fb.getLocalName());
154: assertEquals("fb:like", fb.getNodeName());
155: 156: }

