public static Tag valueOf(String tagName) {
Validate.notNull(tagName);
tagName = tagName.trim().toLowerCase();
Validate.notEmpty(tagName);
synchronized (tags) {
Tag tag = tags.get(tagName);
if (tag == null) {
tag = new Tag(tagName);
tag.setAncestor(defaultAncestor.tagName);
tag.isBlock = false;
tag.canContainBlock = true;
return tag;
}

public Element first() {
return !contents.isEmpty() ? contents.get(0) : null;
}

public int hashCode() {
int result = tagName != null ? tagName.hashCode() : 0;
result = 31 * result + (isBlock ? 1 : 0);
result = 31 * result + (canContainBlock ? 1 : 0);
result = 31 * result + (canContainInline ? 1 : 0);
result = 31 * result + (optionalClosing ? 1 : 0);
result = 31 * result + (empty ? 1 : 0);
return result;
}

private Element popStackToClose(Tag tag) {
int counter = 0;
Element elToClose = null;
for (int i = stack.size() -1; i > 0; i--) {
counter++;
Element el = stack.get(i);
Tag elTag = el.tag();
if (elTag.equals(bodyTag) || elTag.equals(htmlTag)) { // once in body, don't close past body
} else if (elTag.equals(tag)) {
elToClose = el;
break;
if (elToClose != null) {
for (int i = 0; i < counter; i++) {
stack.removeLast();
return elToClose;
}

public String consumeToAny(String... seq) {
StringBuilder accum = new StringBuilder();
while (!queue.isEmpty() && !matchesAny(seq))
accum.append(consume());
return accum.toString();
}

protected void setParentNode(Node parentNode) {
if (this.parentNode != null)
this.parentNode = parentNode;
}

private static void accumulateMatches(Evaluator eval, List<Element> elements, Element element) {
if (eval.matches(element))
elements.add(element);
for (Element child: element.children())
accumulateMatches(eval, elements, child);
}

