private int remainingLength() {
return queue.length() - pos;
}

public Character peek() {
return isEmpty() ? null : queue.charAt(pos);
}

public Element appendChild(Node child) {
Validate.notNull(child);
addChildren(child);
return this;
}

public String absUrl(String attributeKey) {
Validate.notEmpty(attributeKey);
String relUrl = attr(attributeKey);
if (!hasAttr(attributeKey)) {
base = new URL(baseUri);
}
URL abs = new URL(base, relUrl);
return abs.toExternalForm();
}

private Tag setContainDataOnly() {
canContainBlock = false;
canContainInline = false;
preserveWhitespace = true;
return this;
}

public String attr(String attributeKey) {
Validate.notNull(attributeKey);
if (hasAttr(attributeKey))
return attributes.get(attributeKey);
}

private Element findFirstElementByTagName(String tag, Node node) {
if (node.nodeName().equals(tag))
return (Element) node;
for (Node child: node.childNodes) {
Element found = findFirstElementByTagName(tag, child);
if (found != null)
return found;
}
return null;
}

public Node nextSibling() {
if (parentNode == null)
return null; // root
List<Node> siblings = parentNode.childNodes;
Integer index = siblingIndex();
Validate.notNull(index);
if (siblings.size() > index+1)
return siblings.get(index+1);
return null;
}

private Element popStackToSuitableContainer(Tag tag) {
while (!stack.isEmpty()) {
if (last().tag().canContain(tag))
return last();
}
