private Tag setIgnoreEnd(String... tagNames) {
if (tagNames == null || tagNames.length == 0) {
ignoreEndTags = new ArrayList<Tag>(tagNames.length);
for (String name : tagNames) {
ignoreEndTags.add(Tag.valueOf(name));
return this;
}

public boolean matchesCS(String seq) {
return queue.startsWith(seq, pos);
}

public boolean matches(String seq) {
int count = seq.length();
if (count > remainingLength())
return false;
while (--count >= 0) {
if (Character.toLowerCase(seq.charAt(count)) != Character.toLowerCase(queue.charAt(pos+count)))
return false;
return true;
}

public Document normalise() {
Element htmlEl = findFirstElementByTagName("html", this);
if (htmlEl == null)
if (head() == null)
if (body() == null)
normaliseTextNodes(head());
normaliseTextNodes(htmlEl);
normaliseTextNodes(this);
normaliseStructure("head", htmlEl);
normaliseStructure("body", htmlEl);
return this;
}

private Selector(String query, Element root) {
Validate.notNull(query);
query = query.trim();
Validate.notEmpty(query);
Validate.notNull(root);
this.elements = new LinkedHashSet<Element>();
this.query = query;
this.root = root;
this.tq = new TokenQueue(query);
}

public static Elements select(String query, Element root) {
return new Selector(query, root).select();
}

public static TextNode createFromEncoded(String encodedText, String baseUri) {
String text = Entities.unescape(encodedText);
return new TextNode(text, baseUri);
}

public boolean matchChomp(String seq) {
if (matches(seq)) {
pos += seq.length();
return true;
return false;
}
