private Tag setAncestor(String... tagNames) {
if (tagNames == null) {
ancestors = new ArrayList<Tag>(tagNames.length);
for (String name : tagNames) {
ancestors.add(Tag.valueOf(name));
return this;
}

protected int nodeDepth() {
if (parentNode == null)
