void read(Tokeniser t, CharacterReader r) {
String tagName = r.consumeToAny('\t', '\n', '\f', ' ', '/', '>', nullChar).toLowerCase();
t.tagPending.appendTagName(tagName);
switch (r.consume()) {
t.emitTagPending();
t.transition(Data);
break;
}

