void read(Tokeniser t, CharacterReader r) {
String name = r.consumeToAny('\t', '\n', '\f', ' ', '/', '=', '>', nullChar, '"', '\'', '<');
t.tagPending.appendAttributeName(name.toLowerCase());
char c = r.consume();
switch (c) {
t.transition(BeforeAttributeValue);
break;
}

void read(Tokeniser t, CharacterReader r) {
String value = r.consumeToAny('\'', '&', nullChar);
if (value.length() > 0)
t.tagPending.appendAttributeValue(value);
char c = r.consume();
switch (c) {
t.transition(AfterAttributeValue_quoted);
break;
}

private void matches(boolean own) {
tq.consume(own ? ":matchesOwn" : ":matches");
String regex = tq.chompBalanced('(', ')'); // don't unescape, as regex bits will be escaped
Validate.notEmpty(regex, ":matches(regex) query must not be empty");
if (own)
evals.add(new Evaluator.Matches(Pattern.compile(regex)));
}

Evaluator parse() {
tq.consumeWhitespace();
if (tq.matchesAny(combinators)) { // if starts with a combinator, use root as elements
findElements();
while (!tq.isEmpty()) {
boolean seenWhite = tq.consumeWhitespace();
if (tq.matchChomp(",")) {
CombiningEvaluator.Or or = new CombiningEvaluator.Or(evals);
evals.clear();
evals.add(or);
while (!tq.isEmpty()) {
String subQuery = tq.chompTo(",");
or.add(parse(subQuery));
}
} else if (tq.matchesAny(combinators)) {
combinator(tq.consume());
} else if (seenWhite) {
findElements(); // take next el, #. etc off queue
}
if (evals.size() == 1)
return evals.get(0);
}

public static Elements select(String query, Element root) {
return new Selector(query, root).select();
}
